if [ "${SHELL##*/}" = "zsh" ]; then ZSHRC="https://raw.githubusercontent.com/jonzobrist/Bash-Admin-Scripts/master/.zshrc"; pushd ${HOME}; curl -q -o ${HOME}/.zshrc ${ZSHRC}; git clone --recursive https://github.com/sorin-ionescu/prezto.git "${ZDOTDIR:-$HOME}/.zprezto"; setopt EXTENDED_GLOB; for rcfile in "${ZDOTDIR:-$HOME}"/.zprezto/runcoms/^README.md(.N); do ln -s "$rcfile" "${ZDOTDIR:-$HOME}/.${rcfile:t}"; done; popd; else echo "Need to be using ZSH"; fi; chsh -s $(which zsh)
